import { Line } from 'react-chartjs-2';

const TrendsChart = ({ data }) => {
  const chartData = {
    labels: data.map(item => item.Date), // Replace 'Date' with the actual date column name
    datasets: [
      {
        label: 'Average Price',
        data: data.map(item => item.AveragePrice), // Replace with actual column name
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
    ],
  };

  return <Line data={chartData} />;
};

export default TrendsChart;
